package orangehrm;

public class switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String x="a";
switch (x) {
case "a":
	System.out.println("java");
	break;
case "b":
	System.out.println("selenium");
	break;
case "c":
	System.out.println("Qtp");
	break;
default:
	System.out.println("invalid");
	break;
}
	}

}
